class Book{
  String title;
  String src;
  String coverSrc;
  Book(String title,String src,String coverSrc){
    this.title=title;
    this.src=src;
    this.coverSrc=coverSrc;
  }
}
class AppStore {
  Res homePageTag;
  HomePageListRes homePageList;
  String active;
  DetailRes detail;
  DetailTagRes detailTag;
  DefaultRecRes defaultRec;
  /////
  int counter;
  ThemeData theme;
  List<Book> books;
  String currentBook;
  AppStore(int counter, ThemeData y,List<Book> books,Res homePageTag,HomePageListRes homepageList,String active,DetailRes detail,DetailTagRes detailTag,DefaultRecRes defaultRec) {
    this.counter = counter;
    this.theme = y;
    this.active=active;
    this.books=books;
    this.homePageList=homepageList;
    this.homePageTag=homePageTag;
    this.detail=detail;
    this.detailTag=detailTag;
    this.defaultRec=defaultRec;
  }
}

